CREATE TABLE IF NOT EXISTS `core_article` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title_ua` varchar(200),
  `text_ua` text,
  `title_ru` varchar(200),
  `text_ru` text,
  `title_en` varchar(200),
  `text_en` text,
  `date` int(11) NOT NULL,
  `category_id` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=13 ;

--
-- Дамп данных таблицы `core_article`
--

INSERT INTO `core_article` (`id`, `title_ua`, `text_ua`, `date`, `category_id`) VALUES
(1, 'День народження університету', '<p>\r\n	16 жовтня відбулося урочисте святкування 52-го Дня народження університету. Розпочалось воно з найприємнішого для студентів – скорочення робочого дня.\r\n</p>\r\n<p>\r\n	      О 10.15 біля ГУКу було проведено танцювальний флешмоб за участю студентів усіх інститутів ВНТУ. Попри незначну кількість репетицій, студенти досить непогано танцювали, а саме дійство вийшло запальним та підняло настрій як самим учасникам, так і перехожим.\r\n</p>\r\n<p>\r\n	      О 15.00 в актовій залі відбувся святковий концерт, на якому було відмічено заслуги та досягнення викладацького та керівного складу, а також представлено номери наших університетських талантів.\r\n</p>', 1382521470, 1),
(2, 'Квест першокурсника', '<p>\r\n	Перші тижні навчання і вже перші випробування відчули на собі першокурсники ВНТУ, адже 16-17-го вересня відбувся квест «Першокурсника». Цей квест вже неодноразово проводився в нашому університеті і користується великим успіхом у студентів. По-перше це можливість краще познайомитись з територією університету та надалі швидше знаходити потрібні аудиторії, по-друге – нові знайомства та спілкування, ну і звичайно призи. В цьому році за 1-ше місце – 1000грн., за 2-ге – 750 грн., за 3-тє – 500 грн.\r\n</p>\r\n<p>\r\n	       Від ІнАЕКСУ брали участь 2 команди. В перший день стартували трохи невдало, були деякі заминки, але на другий день вони реабілітувались і показали непоганий результат – прийшли до фіналу 4-ми. В результаті отримали грошову винагороду у розмірі 100 грн.\r\n</p>\r\n<p>\r\n	       Як для першого разу – це досить не погано, в них попереду ще багато різних випробувань, так що побажаємо їм успіхів!\r\n</p>', 1382521521, 1),
(3, 'Футбол першокурсників', '<p>\r\n	Вересень виявився досить насиченим на різноманітні заходи для наших першокурсників.  Одним з таких став футбол для першого курсу.\r\n</p>\r\n<p>\r\n	  Перші матчі відбулися 18.09 між Інститутами машинобудування та транспорту і збірною Інститутів менеджменту і екології, де перемогу з рахунком 3:1 отримала збірна ІнМ та ІнЕЕК, та між ІнІТКІ та ІнЕЕЕМ, де з рахунком 2:1 перемогла  команда ІнІТКІ. Команди-переможниці вийшли в півфінал.\r\n</p>\r\n<p>\r\n	  Наступного дня за вихід в півфінал змагались команди ІнБТЕГП та ІнРТЗП. Результатом напруженої гри був рахунок 2:2,  після чого була розіграна серія пенальті за результатами яких в півфінал вийшла команда ІнРТЗП.\r\n</p>\r\n<p>\r\n	  Півфінальні ігри відбулись 20.09. Першими на поле вийшли команди ІнРТЗП та ІнАЕКСУ. Гра було досить напруженою і ні одна з команд не хотіла поступатись, але забитий в другому таймі гол у ворота ІнАЕКСУ послабив командний дух команди, що призвело до їх поразки з результатом 3:0. Наступна гра півфіналістів закінчилась перемогою збірної ІнМ та ІнЕЕК.\r\n</p>\r\n<p>\r\n	  Фінал відбувся в кращих традиціях – напружений, видовищний та інтригуючий. Після закінчення гри відбулось нагородження переможця – збірної ІнМ та ІнЕЕК – кубком, медалями та грамотами. Також були нагородженні інші призери змагань.\r\n</p>\r\n<p>\r\n	  Хотілося б відмітити сумлінну роботу тренера команди ІнАЕКСУ – Мандлюка В. та палку підтримку вболівальників, більшість з яких були студенти першого курсу.\r\n</p>', 1382521560, 1),
(4, 'Ознайомлення з електронними базами даних EBSCO – Applied Science & Technology Source, Legal Source, Education Source, Humanities Source', '<p>\r\n	Вінницька обласна універсальна наукова бібліотека ім. К.А.Тімірязєва запрошує професорсько-викладацький склад, науковців, аспірантів та студентів університету для ознайомлення з електронними повнотекстовими базами даних EBSCO – Applied Science &amp; Technology Source, Legal Source, Education Source, Humanities Source, до яких бібліотека одержала доступ до 26 квітня 2013 року.\r\n</p>\r\n<p style="text-align: center;">\r\n	<img src="/uploads/images/5c21fb9a8edf6d440c0fece2e3591998.jpg">\r\n</p>\r\n<p>\r\n	Вищеперераховані бази містять багато унікальних джерел, які ніколи не були доступні.\r\n</p>\r\n<p>\r\n	Список назв, який доступний за адресою:\r\n</p>\r\n<p>\r\n	<a href="http://www.ebscohost.com/academic/applied-science-technology-source">www.ebscohost.com/academic/applied-science-technology-source</a>\r\n</p>\r\n<p>\r\n	<a href="http://www.ebscohost.com/academic/education-source">www.ebscohost.com/academic/education-source</a>\r\n</p>\r\n<p>\r\n	<a href="http://www.ebscohost.com/academic/humanities-source">www.ebscohost.com/academic/humanities-source</a>\r\n</p>\r\n<p>\r\n	<a href="http://www.ebscohost.com/academic/legal-source">www.ebscohost.com/academic/legal-source</a>\r\n</p>\r\n<p>\r\n	<em>Доступ до баз надається усіма структурними підрозділами бібліотеки.</em>\r\n</p>', 1382521689, 1),
(5, 'Конкурс спільних українсько-литовських науково-технічних проектів на період 2014-2015 років', '<p>\r\n	З 15 березня по 30 квітня 2013 року Державне агентство з питань науки, інновацій та інформатизації України та Міністерство освіти та науки Республіки Литва оголошують конкурс спільних українсько-литовських науково-технічних проектів на період 2014-2015 років.\r\n</p>\r\n<p>\r\n	Конкурс відкритий для наукових лабораторій, науково-дослідних груп вищих навчальних закладів, науково-дослідних установ і підприємств обох країн.\r\n</p>\r\n<p style="text-align: center;">\r\n	<img src="/uploads/images/52c778039fe08118af5874a00035ba1a.jpg">\r\n</p>\r\n<p>\r\n	До участі у конкурсі приймаються проекти за такими пріоритетними напрямками:\r\n</p>\r\n<p>\r\n	– Інформаційні та нові виробничі технології (лазерні, прецизійні, механотронні, робототехнічні, плазменні та інші)\r\n</p>\r\n<p>\r\n	– Енергетика та енергоефективність;\r\n</p>\r\n<p>\r\n	– Екологія та раціональне природокористування;\r\n</p>\r\n<p>\r\n	– Науки про життя, нові технології лікування та профілактики найпоширеніших хвороб, дослідження у сфері біотехнології, біоінженерії та генетики;\r\n</p>\r\n<p>\r\n	– Нові речовини та матеріали;\r\n</p>\r\n<p>\r\n	– Соціальні та гуманітарні науки.\r\n</p>\r\n<p>\r\n	детальніше — <a href="http://www.dknii.gov.ua/component/content/article/73-2010-10-22-08-33-32/1052--2014-2015-">http://www.dknii.gov.ua/component/content/article/73-2010-10-22-08-33-32/1052—2014-2015-</a>\r\n</p>', 1382521749, 1),
(6, 'Науковці ВНТУ впроваджують енергозберігаючі технології', '<p>\r\n	 19 березня у Вінницькій міській раді (ВМР) відбулася нарада щодо реалізації та впровадження нових енергозберігаючих технологій. У нараді взяли участь директор департаменту енергетики і транспорту міської ради Олексій Підоченко, директор комунального підприємства ВМР «Вінницяміськтеплоенерго» Сергій Коваленко, директор департаменту освіти міської ради Володимир Буняк, науковці Вінницького національного технічного університету, а також провідні фахівці ВМР.\r\n</p>\r\n<p style="text-align: center;">\r\n	 <img src="/uploads/images/92195236db99f7d56bd5ce1b20e358d8.jpg" style="width: 609px;">\r\n</p>\r\n<p>\r\n	 Від ВНТУ були представлено проекти:<br>\r\n	 • Застосування напівпровідникових джерел світла для енергозберігаючих освітлювальних систем в муніципальних установах та підприємствах міста (проректор з наукової роботи ВНТУ, зав. каф. ЗФФ, д.т.н., проф. Сергій Павлов);<br>\r\n	 • Покращення показників дизеля використанням суміші газового та біодизельного палив (заступник директора з наукової роботи та міжнародного співробітництва Інституту МТ, д.т.н., проф. Андрій Поляков);<br>\r\n	 • Вологомір природного газу (вхідний контроль газу міських котелень) (зав. каф. МЕ, д.т.н., проф. Йосип Білинський, представляв проект директор ІнМАД, зав. каф. КЕЕМІГ, д.т.н., проф. Віталій Мокін);<br>\r\n	 • Оптико-абсорбційна система газового аналізу з компенсацією дестабілізуючих факторів (зав. каф. МПА, д.т.н., проф. Володимир Кучерук, аспірант каф. МПА Ігор Дудатьєв);<br>\r\n	 • Радіовимірювальна система для моніторингу температури, вологості, тиску (зав. каф. РТ, д.т.н., проф. Олександр Осадчук);<br>\r\n	 • Розробка та впровадження установок по виробництву біогазу з відходів (доц. каф. КСУ, к.т.н. Таїсія Боровська)<br>\r\n	 • Енергетичний аудит муніципальних споживачів ( доц., каф. ЕСЕЕМ, к.т.н., Олександр Демов).<br>\r\n	 Величезну роботу щодо організації такої наради проведено проф. Віталієм Мокіним, який є куратором співпраці ВНТУ та Вінницької міської ради.<br>\r\n	 У підсумку Олексієм Підоченком було відзначено актуальність та інноваційність даних проектів, запропоновано відповідним експертам взяти на озброєння представлені розробки.\r\n</p>', 1382521857, 1),
(7, 'ВЕСНУ В УНІВЕРСИТЕТІ РОЗПОЧАЛА МІЖНАРОДНА КОНФЕРЕНЦІЯ', '<p>\r\n	  1 березня у нашому виші відбулась ІІ Міжнародна науково-практична конференція «Сучасні тенденції розвитку фінансових та інноваційно-інвестиційних процесів в Україні». Зорганізувала це масштабне наукове дійство кафедра фінансів\r\n</p>\r\n<p>\r\n	      Конференція отримала широкий відгук серед науковців як України, так і близького зарубіжжя, зокрема Азербайджану, Грузії, Казахстану, Росії.Для участі у її роботі було подано 135 доповідей, серед яких 18 від докторів наук, 44 від кандидатів наук. Серед бажаючих взяти участь у науково-практичній конференції представники 38 національних ВНЗ та науково-дослідних установ.\r\n</p>\r\n<p style="text-align: center;">\r\n	<img src="/uploads/images/dff7ef66cfa159956582612a22cbf4a7.jpg" style="width: 573px;">\r\n</p>\r\n<p>\r\n	      Привітав учасників конференції <strong>ректор ВНТУ, доктор технічних наук, професор Володимир Грабко</strong>, побіжно ознайомивши гостей із досягненнями нашого вишу.\r\n</p>\r\n<p>\r\n	      Обговорювались на конференції проблеми за такими основними напрямами:\r\n</p>\r\n<p>\r\n	      Øфінансовий механізм соціально-економічного розвитку України;\r\n</p>\r\n<p>\r\n	      Øінноваційні процеси та їх вплив на конкурентоспроможність національної економіки;\r\n</p>\r\n<p>\r\n	      Øсучасні проблеми інвестиційної діяльності в економіці України;\r\n</p>\r\n<p>\r\n	      Øактуальні проблеми управління сучасним підприємством.\r\n</p>\r\n<p>\r\n	      Наукову дискусію на пленарному засіданні конференції розпочав <strong>заслужений діяч науки і техніки України, дійсний член Академії економічних наук України, доктор економічних наук, професор кафедри фінансів Київського національного університету імені Тараса Шевченка</strong><strong>Захарій Варналій. </strong>У своїй доповіді«Стимулювання інноваційної активності малих підприємств в контексті забезпечення конкурентоспроможності економіки України» Захарій Степанович охарактеризував основні домінанти сучасного соціально-економічного розвитку, обґрунтував об’єктивну необхідність впровадження інноваційної моделі розвитку економіки України, розкрив її сутність та структуру, зупинився на проблемах фінансування інноваційної діяльності, зокрема малих підприємств, визначив чинники підвищення інноваційного потенціалу вітчизняної економіки, назвав об’єктивні причини, які перешкоджають створенню в Україні кластерів.\r\n</p>\r\n<p>\r\n	      Продовжив дебати <strong>проректор з науково-педагогічної роботи – перший проректор Хмельницького національного університету, завідувач тамтешньої кафедри обліку та аудиту,</strong><strong>доктор економічних наук</strong>, <strong>професор Михайло Войнаренко.</strong> У своєму ґрунтовному науковому повідомленні «Інституційна рівновага в соціально-економічних системах» Михайло Петрович акцентував на сучасних тенденціях розвитку інституційного середовища, відзначив важливість використання теорій інституціоналізму при розробці стратегій регіонального розвитку, показав, що застосування інституційно-мережевої взаємодії при наявному ресурсному потенціалі регіональної економіки дозволяє віднайти нові джерела економічного зростання території на якісно новій інноваційній основі.\r\n</p>\r\n<p>\r\n	      Жвавий інтерес аудиторії викликав також виступ <strong>Євгенії Чернявської, </strong><strong>доктора економічних наук, професора Східноукраїнського національного університету імені Володимира Даля, —</strong> «Корпоративне управління і проблема залучення зовнішніх інвестицій». Професор визначила найважливіші переваги корпорацій, проаналізувала причини низького рівня залучення зовнішніх інвестицій в Україну. Євгенія Іванівнааргументовано обґрунтувала: для забезпечення підприємств необхідними інвестиціями необхідно внести зміни у модель економічних реформ, які б забезпечили уз­годження інтересів учасників економічної діяльності, розвиток партнер­ських відносин на рівні підприємства і суспільства — це дозволить ­ефективно поєднувати ресурси і їхній розподіл відповід­но до закономірностей XXI століття.\r\n</p>\r\n<p>\r\n	      Далі конференцію продовжила низка актуальних виступів, які спричинили жваву дискусію. Зокрема, застосовуючи математичний апарат теоретико-ігрового моделювання та базуючись на гіпотезі раціональної поведінки, <strong>кандидат фізико-математичних наук, доцент нашого університету</strong><strong>Анатолій Шиян </strong>промоделював поведінку підприємства на основі функції корисності з позиції максимальної її задоволеності.\r\n</p>\r\n<p>\r\n	      Про наукові погляди на вибір підприємствами стратегії швидкого зростання економічного потенціалу розповів <strong>кандидат економічних наук, доцент кафедри фінансів Національного університету харчових технологій</strong> <strong>Юрій Гудзь</strong>.\r\n</p>\r\n<p>\r\n	<strong>Здобувач Інституту міжнародних відносин КНУ імені Тараса Шевченка</strong> <strong>Юрій Мороз </strong>виступив на тему «Інвестиції та інвестиційний клімат в Україні».\r\n</p>\r\n<p>\r\n	      Ролі економічної безпеки у створенні сприятливих умов для сталого економічного зростання та підвищення конкурентоспроможності національної економіки, прискорення структурних та інституційних змін в економіці, оздоровлення фінансово-кредитної сфери, забезпечення збалансованого розвитку бюджетної сфери був присвячений виступ <strong>кандидата економічних наук, доцента Академії митної служби України</strong> <strong>Наталії Архірейської.</strong>\r\n</p>\r\n<p>\r\n	      Підсумок пленарного засідання конференції підбив <strong>завідувач кафедри фінансів ВНТУ, доктор економічних наук, професор Віталій</strong> <strong>Зянько</strong>, який особливо акцентував на сучасних проблемах інноваційного розвитку економіки України.\r\n</p>\r\n<p>\r\n	      Надалі тривала робота у секціях.\r\n</p>\r\n<p>\r\n	      Докладно з доповідями учасників конференції можна ознайомитися у збірнику «Сучаснітенденції розвитку фінансових та інноваційно-інвестиційних процесів в Україні: Матеріали міжнародної науково-практичної конференції». – Вінниця : ВНТУ, 2013.\r\n</p>', 1382521937, 1),
(8, 'ВНТУ — ЛІДЕР НАУКОВОЇ ДІЯЛЬНОСТІ', '<p>\r\n	 Наш виш відзначений Диплом IVміжнародної виставки «Сучасні заклади освіти-2013» за вагомий внесок у розвиток національної освіти\r\n</p>\r\n<p>\r\n	   Відбувалось масштабне освітянське дійство у столиці впродовж 28 лютого – 2 березня. 960 закладів освіти, серед яких не лише вітчизняні, а й представники дальнього та ближнього зарубіжжя, демонстрували абітурієнтам свої досягнення, знайомили з особливостями навчання.\r\n</p>\r\n<p style="text-align: center;">\r\n	 <img src="/uploads/images/cb1c8de9e68dbfa9c80c5bb117ab7a6e.jpg" style="width: 618px;">\r\n</p>\r\n<p>\r\n	   Зорганізували цей потужний західМіністерство освіти і науки, молоді і спорту України, Національна академія педагогічних наук України, компанія «Виставковий світ».\r\n</p>\r\n<p>\r\n	   — Потрібно, щоб здобутки окремих навчальних закладів стали відомими і були перейняті всією освітою України, щоб наша освіта готувала людину до життя і діяльності у 21 столітті. — Зазначив президент НАПНУ Василь Кремень. — Також сьогоднішня виставка – це ще одна можливість для майбутніх абітурієнтів звірити свої перспективи, оцінити навчальні заклади і обрати свій життєвий шлях.\r\n</p>\r\n<p>\r\n	 <strong>Наш університет удостоєний також Диплома і відзнаки «Лідер наукової та науково-технічної діяльності». ВНТУ отримав ще </strong><strong>Sertifіkat</strong><strong>е</strong><strong> якості наукових публікацій</strong>.\r\n</p>\r\n<p>\r\n	   Ректор ВНТУ Володимир Грабко за особистий внесок у зміцнення освітянського потенціалу України нагороджений Почесною грамотою.\r\n</p>', 1382522104, 1),
(9, 'КРАСНОЩОКЕ ЯБЛУКО ДЛЯ АНАСТАСІЇ КРАСНОЩОКИ ВІД ПАРІСА', '<p>\r\n	 Найпопулярнішим серед різноманіття університетських заходів був і залишається конкурс «Міс ВНТУ». Щоразу під час його проведення глядацька зала заповнюється вщерть, і Ньютону було б за цих умов складно зробити своє геніальне відкриття, бо навіть яблуку ніде впасти\r\n</p>\r\n<p>\r\n	 Звичайно, березень – не яблучний сезон, але одне яблучко 5 березня таки було вручено Парісом (якого уособлювало журі конкурсу) найкращій серед претенденток на титул «Міс ВНТУ - 2013».\r\n</p>\r\n<p style="text-align: center;">\r\n	 <img src="/uploads/images/0c38bb505404a034bfeb2352aaddbba9.jpg" style="width: 358px;">\r\n</p>\r\n<p>\r\n	 Журі намагалось бути максимально виваженим у своїх рішеннях, щоб не спровокувати нову Троянську війну поміж армій вболівальників університетських красунь.\r\n</p>\r\n<p>\r\n	 Уже в конкурсі «Дефіле» викреслились кілька ймовірних претенденток на підсумкову перемогу, не змінилась ситуація і після «Візитки», яка цього року була в форматі відео-ролика. Учасниці та їхні команди виявили неабияку винахідливість та оригінальність в підготовці сюжетів, порадували операторські рішення, несподівані образи конкурсанток.\r\n</p>\r\n<p>\r\n	 Інтелектуальний конкурс по-справжньому змусив виявити свою ерудованість університетських красунь, логічність та образність мислення. Неофіційний титул «Варвари премудрої» в цьому змаганні виборола Таня Калагуряну, але й інші дівчата були зовсім поруч.\r\n</p>\r\n<p>\r\n	 Одним з вирішальних моментів суперництва за першість, напевно, став творчий конкурс. Варто відзначити, що більшість з представлених виступів постали у вигляді міні-вистав, з цікавими образами, високою виконавською майстерністю. Проте, декому завадило хвилювання чи може ще якісь причини, щоб яскраво та впевнено показати всі свої можливості й таланти на університетській сцені під час свого творчого номеру.\r\n</p>\r\n<p>\r\n	 Останні штрихи до свого портрету «Міс ВНТУ – 2013» представниці восьми інститутів нашого університету зробили в хореографічній постановці під джазову композицію у виконанні Єви Нечепуренко.\r\n</p>\r\n<p>\r\n	 Жодна з учасниць не була обділена увагою спонсорів, зокрема всі вони отримали приємні подарунки від студентського профкому ВНТУ, а колишній ректор Борис Мокін вручив свої власні призи (у гривневому вигляді) Анастасії Каруцяк та Анастасії Краснощоці.\r\n</p>\r\n<p>\r\n	 Приз глядацьких симпатій цьогоріч отримала представниця Інституту машинобудування та транспорту Аліна Кислицька, а на думку журі найліпші образи жіночої краси вдалось створити Анастасії Каруцяк (ІнЕЕЕМ), Дар’ї Шевчук (ІнБТЕГП), які отримали титул Віце-міс ВНТУ та Анастасії Краснощоці, котра була проголошена Міс ВНТУ 2013 року й отримала жадане яблучко від Паріса.\r\n</p>\r\n<p>\r\n	 <strong>Анатолій ТЕКЛЮК,</strong>\r\n</p>\r\n<p>\r\n	 <strong>заступник директора ГЦВР,</strong>\r\n</p>\r\n<p>\r\n	 <strong>кандидат філософських наук, доцент</strong>\r\n</p>', 1382522245, 1),
(10, 'ПЕРШИЙ КРОК ДО ПЕРШОСТІ СВІТУ', '<p>\r\n	 20 квітня у Вінницькому національному технічному університеті відбудеться перший етап всеукраїнської студентської командної олімпіади з програмування — 1/8 першості світу. До участі в олімпіаді запрошуються студенти усіх вишів III-IV рівнів акредитації Вінницької області, у яких вивчається програмування — як впродовж п''яти років навчання, так і вмежах однієї - двох дисциплін\r\n</p>\r\n<p style="text-align: center;">\r\n	 <img src="/uploads/images/c537e79dc5cc74f5c91fb8c58e7d3330.jpg" style="width: 489px;">\r\n</p>\r\n<p>\r\n	 Під час олімпіади командам (3 студента) буде запропоновано для розв’язання до 15 задач різного рівня складності. Тривалість олімпіади складає 5 годин.\r\n</p>\r\n<p>\r\n	 Олімпіада сприяє мотивації студентів до напруженої самостійної роботи з підвищення свого професійного рівня в галузі програмування, підвищенню рівня знань з інформатики, дискретної математики, математичної логіки, розробки ефективних алгоритмів і програм.\r\n</p>\r\n<p>\r\n	 Світова студентська першість з програмування — це наймасовіша студентська олімпіада в світі. Участь у таких змаганнях може стати фантастичним стартом для кар’єри.\r\n</p>\r\n<p>\r\n	 Для програміста дуже важливим є уміння працювати в команді. Але цьому не навчають університети. А ось тут команда з трьох студентів і один комп’ютер — учасники мають налагоджувати співдію в команді і водночас витримувати конкуренцію з іншими командами. Переможе команда, яка швидше напише програми, що розв’яжуть найбільшу кількість задач.\r\n</p>', 1382522304, 1),
(11, 'У ВНТУ завершилась олімпіада до Дня космонавтики', '<p>\r\n	   Переможцем олімпіади став студент Вінницького національного технічного університету Юрій Іванов\r\n</p>\r\n<p>\r\n	 <strong> </strong>\r\n</p>\r\n<p>\r\n	   З 9 по 12 квітня на базі Вінницького національного технічного університету відбулась чотирнадцята олімпіада зі спеціальності «Комп’ютерні системи управління та автоматики» присвячена до Дня космонавтики. Цьогоріч в ній прийняло участь 49 студентів з усіх куточків України.\r\n</p>\r\n<p style="text-align: center;">\r\n	 <img src="/uploads/images/fd3d55a03a4074fc342f9371a5bbcb39.jpg" style="width: 526px;">\r\n</p>\r\n<p>\r\n	   Програма олімпіади була розрахована на 3 дні. У перший день майбутні інженери демонстрували свої теоретичні знання. На другий день перед ними ставились практичні завдання, з якими студенти мали впоратись за 4 години. Ну і на фінал, у останній день, було проведено урочисте закриття з врученням цінних призів переможцям.\r\n</p>\r\n<p style="text-align: center;">\r\n	 <img src="/uploads/images/72252272eb970e324f8bdf2654f7c8b1.jpg" style="width: 526px;">\r\n</p>\r\n<p>\r\n	   Як каже директор Інститут автоматики, електроніки та комп’ютерних систем управління , дана олімпіада створена не лише для збагачення наукового багажу студентів, але й для обміну досвіду між викладачами провідних вишів країни.\r\n</p>\r\n<p style="text-align: center;">\r\n	 <img src="/uploads/images/b1f818d87c82aaa30a6e568addca91d0.jpg" style="width: 526px;">\r\n</p>\r\n<p>\r\n	   Ну і звісно ж, не відсутній був і змагальний процес. Розрив між призовими місцями був мінімальний, тому боротьба хоч і несла товариський характер та була запеклою.\r\n</p>\r\n<p style="text-align: center;">\r\n	 <img src="/uploads/images/61dbe432f59c4432e304d1ff987939a6.jpg" style="width: 526px;">\r\n</p>\r\n<p>\r\n	   Третю сходинку в індивідуальному заліку розділили Скриль Артем з Кременчуцького національного університету ім. Михайла Остроградського та Якимчук Михайло з Чернівецького національного університету ім. Юрія Федьковича.\r\n</p>\r\n<p style="text-align: center;">\r\n	 <img src="/uploads/images/c2dcfbef89209a005d5d9deb90d2b846.jpg" style="width: 526px;">\r\n</p>\r\n<p>\r\n	   На другій сходинці, зупинившись за крок до бажаної перемоги, опинились Пилипенко Інна з Вінницького національного технічного університету та Крутько Віталій – представник Харківського національного університету радіоелектроніки.\r\n</p>\r\n<p style="text-align: center;">\r\n	 <img src="/uploads/images/7cd2a9181322cbff4a0f9ca2899a0b38.jpg" style="width: 526px;">\r\n</p>\r\n<p>\r\n	   Переможцем олімпіади став студент Вінницького національного технічного університету Юрій Іванов. Він уже не вперше перемагає на головному турнірі країни в галузі комп’ютерних систем управління та автоматики. Рік назад Юрій також виборов перше місце, а вже цьогорічний результат підтвердив його високий рівень знань.\r\n</p>\r\n<p style="text-align: center;">\r\n	 <img src="/uploads/images/918e8ce0f5506341c8155717ba35b2ad.jpg" style="width: 526px;">\r\n</p>\r\n<p>\r\n	   У командному заліку третю сходинку посіла Донбаська державна машинобудівна академія, на другому місці розташувався Харківський національний університет радіоелектроніки, а перше місце виборола команда Інститут автоматики, електроніки та комп’ютерних систем управління вінницького політеху.\r\n</p>', 1382532267, 1),
(12, 'as', '<p>\r\n	saa\r\n</p>', 1383423488, 1);

